<?php/** * This component detects modules/components/plugins/legacy from * project source code structure and manages them. *  * @package \cms\textTemplate * @category component * @author Mojtaba Rajabzadeh <mjr.rajabzadeh@gmail.com> */class textTemplateService extends \f\service{    /**     *     * @var shortcutMapper      */    private $mapper ;    public function __construct ()    {        $this->mapper = \f\dalFactory::make ( 'cms.textTemplate' ) ;    }    public function textTemplateList ()    {        return \f\ttt::dal ( 'cms.textTemplate.textTemplateList',                             $this->request->getAssocParams () ) ;    }    public function textTemplateSave()    {        $params = $this->request->getAssocParams () ;        if($params['id'])        {             $result = \f\ttt::dal ( 'cms.textTemplate.textTemplateSaveEdit', $params ) ;             $msg=\f\ifm::t ( 'textTemplateSaveEdit' );             $reset=FALSE;        }        else        {             $result = \f\ttt::dal ( 'cms.textTemplate.textTemplateSave', $params ) ;             $msg=\f\ifm::t ( 'textTemplateSave' );             $reset=TRUE;        }                       if ( $result )        {            $data = array ( 'result'  => 'success', 'message' => $msg, 'reset' => $reset ) ;        }        else        {            $data = array ( 'result'  => 'error', 'message' => \f\ifm::t ( 'dbError' ) ) ;        }                return $data;    }     public function textTemplateDelete ()    {        return \f\ttt::dal ( 'cms.textTemplate.textTemplateDelete',                             $this->request->getAssocParams () ) ;    }    public function textTemplateStatus ()    {        return \f\ttt::dal ( 'cms.textTemplate.textTemplateStatus',                             $this->request->getAssocParams () ) ;    }        public function getTextTemplateById ()    {        return \f\ttt::dal ( 'cms.textTemplate.getTextTemplateById',                             $this->request->getAssocParams () ) ;    }        public function getTextTemplateList ()    {        return \f\ttt::dal ( 'cms.textTemplate.getTextTemplateList',                             $this->request->getAssocParams () ) ;    }             }